mapper.select:
select
id, city_id, oder_id, service_type, amount, patientEscort_id, income, phone_number, start_time, end_time,
patient_id, create_by, create_time, update_by, update_time, status, del_flag
from order_list


        and id = #{id}


        and city_id = #{cityId}


        and oder_id = #{oderId}


        and service_type = #{serviceType}


        and amount = #{amount}


        and patientEscort_id = #{patientescortId}


        and income = #{income}


        and phone_number = #{phoneNumber}


        and start_time = #{startTime}


        and end_time = #{endTime}


        and patient_id = #{patientId}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and status = #{status}


        and del_flag = #{delFlag}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from order_list


        and id = #{id}


        and city_id = #{cityId}


        and oder_id = #{oderId}


        and service_type = #{serviceType}


        and amount = #{amount}


        and patientEscort_id = #{patientescortId}


        and income = #{income}


        and phone_number = #{phoneNumber}


        and start_time = #{startTime}


        and end_time = #{endTime}


        and patient_id = #{patientId}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and status = #{status}


        and del_flag = #{delFlag}

select (select city from city where id = city_id limit 1) as 'city', oder_id, service_type, amount, (select name from patient_escort_list where id = patientEscort_id limit 1) as 'patientEscortName', income, phone_number, status from order_list
and service_type = #{serviceType}
and city_id = #{cityId}
and patientEscort_id in (select id from patient_escort_list where name = #{patientEscortName})
and phone_number = #{phoneNumber}
and patientEscort_id in (select id from patient_escort_list where name like CONCAT('%', #{patientEscortNameForVague}, '%'))
and oder_id like CONCAT('%', #{orderIdForVague}, '%')
and hospital_id = #{hospitalId}
limit #{page},#{size}

select
count(*)
from
order_list


        and service_type = #{serviceType}


        and city_id = #{cityId}


        and patientEscort_id in
        (select id from patient_escort_list where name = #{patientEscortName})


        and phone_number = #{phoneNumber}


        and patientEscort_id in
        (select id from patient_escort_list where name like CONCAT('%', #{patientEscortNameForVague}, '%'))


        and oder_id like CONCAT('%', #{orderIdForVague}, '%')


        and hospital_id = #{hospitalId}
